import React from "react";
import "./selectmenu.css";
import { useState } from "react";
import { Container } from "reactstrap";


function SelectionMenu() {
    const [showtext, setShowtext] = useState(false);

    const handletextSite=(e)=>{
        const getsite = e.target.value;
        console.log(getsite);
    }

    const handletextPartner=(e)=>{
        const getpartner = e.target.value;
        console.log(getpartner);
    }


    return (

        <React.Fragment>
            <section>
                <form>
                    <div style={{background: 'lightblue',alignItems: 'left', margin: '10px', width: 'auto', display: "left",
                padding: '5px 0 5px 5px'}}>
                        <div style={{alignItems: 'center'}}>
                            <label>Select local site</label>
                                <div className="dropdown">
                                    <div className="dropdown-content">   
                                        <select name="site" className="dropdown-item" onChange={(e)=>handletextSite(e)}>
                                            <option  value="1">AFHOME</option>
                                            <option value="2">NPRO</option>
                                            <option value="3">SPTD</option>
                                        </select>
                                    </div>
                                </div>
                        </div>
                    </div>
                </form>
            </section>
        </React.Fragment>
    );
}

export default SelectionMenu
