import React from "react";
import "./selectmenu.css";
import { useState } from "react";
import { Card, CardBody, Container, FormGroup } from "reactstrap";


function SelectionMenu() {
    const [showtext, setShowtext] = useState(false);

    const handletextSite=(e)=>{
        const getsite = e.target.value;
        console.log(getsite);
    }

    const handletextPartner=(e)=>{
        const getpartner = e.target.value;
        console.log(getpartner);
    }


    return (

        <React.Fragment>
            <section>
                <Card style={{height: '200px', borderBlockStyle: '10px'}}>
                    <CardBody>
                <form style={{height: '90px', backgroundColor: 'white'}}>
                    <FormGroup>
                        <div>
                            <label>Select local site</label>
                                <div className="dropdown">
                                    <div className="dropdown-content">   
                                        <select name="site" className="dropdown-item" onChange={(e)=>handletextSite(e)}>
                                            <option  value="1">AFHOME</option>
                                            <option value="2">NPRO</option>
                                            <option value="3">SPTD</option>
                                        </select>
                                    </div>
                                </div>
                        </div>
                    </FormGroup>
                    </form>
                    <form>
                    <FormGroup>
                        <div>
                    <label>Select Partner</label>
                    <div className="dropdown">
                    <div className="dropdown-content">   
                        <select name="partner" className="dropdown-item" onChange={(e)=>handletextPartner(e)}>
                            <option  value="1">All Partner</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                        </select>
                    </div>
                </div>
                    </div>
                    </FormGroup>
                    </form>
                    </CardBody>
                    </Card>
            </section>
        </React.Fragment>
    );
}

export default SelectionMenu
