import React, { useState,useEffect } from "react";
import "./selectmenu.css";
import { Card, CardBody, CardHeader, FormGroup, Label, Input } from "reactstrap";
import base_url from "../api/bootapi";
import axios from "axios";
import AsyncSelect from "react-select";


function SelectionMenu() {

    const getAllSitesFromServer = () => {
        axios.get(`${base_url}/sites`).then(
            (response) =>{
               // console.log(response);
               console.log(response.data);
               setSites(response.data);
            },
            (error) => {
                console.log(error);
            }
        );

    };

    const getAllPartnersFromServer = (getpartnerserver) => {
         axios.get(`${base_url}/sites/partners/${getpartnerserver}`).then(
             (response) =>{
                // console.log(response);
                console.log(response.data);
                setSitePartners(response.data);
             },
             (error) => {
                 console.log(error);
             }
         );

    };

    useEffect(() => {
        getAllSitesFromServer();
    }, []);

    const [sites, setSites] = useState([]);
    const [SitePartners, setSitePartners] = useState([]);

    //const sites = ['AFHOME','NPRO','SPTD']
    //   const SitePartners = {
    //       'AFHOME': ['All Partner','A', 'F', 'H', 'O', 'M', 'E'],
    //       'NPRO': ['All Partner','N', 'P', 'R', 'O'],
    //       'SPTD': ['All Partner','SP', 'TD'],
    //   }

    const [selectedsite, setselectedsite] = useState('')

    const handleInputChange = value => {
        setselectedsite(value);
        console.log(selectedsite);
    }
       // console.log(selectedsite);
         function handletextSite(e) {
             setselectedsite(e.target.value);
             //console.log(selectedsite);
             getAllPartnersFromServer(selectedsite);
         }
        
        const [selectedpartner, setselectedpartner] = useState('')
        console.log(selectedpartner);
    function handletextPartner(e){
        setselectedpartner(e.target.value);
        console.log(selectedpartner);

    }


    return (

        <React.Fragment>
            <section>
                <Card style={{height: '700px', borderBlockEndColor: 'black', width: '350px'}}>
                    <Card style={{height: '230px', borderBlockEndColor: 'black'}}>
                    <CardHeader style={{fontWeight:'bold',backgroundColor: 'lightgreen', height: '30px'}}><h10>Partners</h10>
                        </CardHeader>
                    <CardBody>
                        

                <form style={{height: '90px', backgroundColor: 'white'}}>
                    <FormGroup>
                        <div>
                            <Label style={{fontWeight:'bold', fontSize: '15px', color: 'blue'}}>Select local site</Label>
                                <div className="dropdown">
                                    <div className="dropdown-content">   
                                        <select name="site" className="dropdown-item" onInput={handleInputChange} onChange={(e)=>handletextSite(e)}>
                                            <option>---Select here---</option>
                                            {
                                                sites.map(site => (
                                                    <option key = {site.siteId}>{site.siteName}</option>
                                                ))
                                            }
                                            console.log(e.target.key);
                                        </select>
                                    </div>
                                </div>
                        </div>
                    </FormGroup>
                    </form>
                    <form>
                    <FormGroup>
                        <div>
                    <Label style={{fontWeight:'bold', fontSize: '15px', color: 'blue'}}>Select Partner</Label>
                    <div className="dropdown">
                    <div className="dropdown-content">   
                        <select name="partner" className="dropdown-item" onChange={(e)=>handletextPartner(e)}>
                            <option>Select here</option>
                            {
                                SitePartners.map(partner => (
                                    <option key = {partner.partnersname}>{partner.partnersname}</option>
                                ))
                            }
                        </select>
                    </div>
                </div>
                    </div>
                    </FormGroup>
                    </form>
                    </CardBody>
                    </Card>

                    <Card style={{height: '270px', borderBlockEndColor: 'black'}}>
                        <CardHeader style={{fontWeight:'bold',backgroundColor: 'lightgreen', height: '30px'}}><h10>Period</h10>
                        </CardHeader>
                    <CardBody>
                    <form style={{height: '90px', backgroundColor: 'white'}}>
                    <FormGroup>
                        <div>
                            <label style={{fontWeight:'bold', fontSize: '15px', color: 'blue'}}>Last{' '}
                            <input style={{width: '50px', paddingLeft: '10px'}} type="text" id="days" value = "1"></input>
                            {' '}days</label>
                            </div>          
                            </FormGroup>
                            <FormGroup check>
                                <Input id="checkbox2" type="checkbox"/> {' '}
                                <Label check>Custom</Label>
                             </FormGroup>
                             <FormGroup>
                             <Label check>Start date</Label>
                                <Input type="date"/>
                                
                             </FormGroup>
                             <FormGroup>
                             <Label check>End date</Label>
                                <Input type="date"/>
                                
                             </FormGroup>
                            </form>
                    </CardBody>
                    </Card>

                    <Card style={{height: '200px', borderBlockEndColor: 'black'}}>
                        <CardHeader style={{fontWeight:'bold',backgroundColor: 'lightgreen', height: '30px'}}><h10>Document Type</h10>
                        </CardHeader>
                    <CardBody>
                    <form style={{height: '90px', backgroundColor: 'white'}}>
                    <FormGroup>
                        <div>
                            <input style={{width: '300px', paddingLeft: '10px'}} type="text" id="days" value = "1"></input>
                            </div>          
                            </FormGroup>
                            </form>
                            </CardBody>
                            </Card>
                    </Card>
            </section>
        </React.Fragment>
    );
}

export default SelectionMenu
